use crate::{
    messages::{
        encoding::{encode_message, MAX_MESSAGE_CONTENT_LEN, MESSAGE_EXPANDED_METADATA_LEN},
        encryption::{aes128::AES128, message_encryption::MessageEncryption},
        logs::utils::prefix_with_tag,
        msg_type::PARTIAL_NOTE_PRIVATE_MSG_TYPE_ID,
    },
    note::note_interface::NoteType,
    utils::array,
};
use crate::protocol::{
    address::AztecAddress,
    constants::PRIVATE_LOG_SIZE_IN_FIELDS,
    traits::{FromField, Packable, ToField},
};

/// The number of fields in a private note message content that are not the note's packed representation.
pub(crate) global PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN: u32 = 4;
pub(crate) global PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_OWNER_INDEX: u32 = 0;
pub(crate) global PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_STORAGE_SLOT_INDEX: u32 = 1;
pub(crate) global PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RANDOMNESS_INDEX: u32 = 2;
pub(crate) global PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_NOTE_COMPLETION_LOG_TAG_INDEX: u32 = 3;

/// Partial notes have a maximum packed length of their private fields bound by extra content in their private message
/// (e.g. the storage slot, note completion log tag, etc.).
pub global MAX_PARTIAL_NOTE_PRIVATE_PACKED_LEN: u32 =
    MAX_MESSAGE_CONTENT_LEN - PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN;

// TODO(#16881): once partial notes support delivery via an offchain message we will most likely want to remove this.
pub fn compute_partial_note_private_content_log<PartialNotePrivateContent>(
    partial_note_private_content: PartialNotePrivateContent,
    owner: AztecAddress,
    storage_slot: Field,
    randomness: Field,
    recipient: AztecAddress,
    note_completion_log_tag: Field,
) -> [Field; PRIVATE_LOG_SIZE_IN_FIELDS]
where
    PartialNotePrivateContent: NoteType + Packable,
{
    let message_plaintext = encode_partial_note_private_message(
        partial_note_private_content,
        owner,
        storage_slot,
        randomness,
        note_completion_log_tag,
    );
    let message_ciphertext = AES128::encrypt(message_plaintext, recipient);

    prefix_with_tag(message_ciphertext, recipient)
}

/// Creates the plaintext for a partial note private message (i.e. one of type [`PARTIAL_NOTE_PRIVATE_MSG_TYPE_ID`]).
///
/// This plaintext is meant to be decoded via [`decode_partial_note_private_message`].
pub fn encode_partial_note_private_message<PartialNotePrivateContent>(
    partial_note_private_content: PartialNotePrivateContent,
    owner: AztecAddress,
    storage_slot: Field,
    randomness: Field,
    note_completion_log_tag: Field,
    ) -> [Field; PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN + <PartialNotePrivateContent as Packable>::N + MESSAGE_EXPANDED_METADATA_LEN]
where
    PartialNotePrivateContent: NoteType + Packable,
{
    let packed_private_content = partial_note_private_content.pack();

    // If PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_NON_NOTE_FIELDS_LEN is changed, causing the assertion below to fail, then
    // the encoding below must be updated as well.
    std::static_assert(
        PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN == 4,
        "unexpected value for PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_NON_NOTE_FIELDS_LEN",
    );

    let mut msg_content =
        [0; PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN + <PartialNotePrivateContent as Packable>::N];
    msg_content[PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_OWNER_INDEX] = owner.to_field();
    msg_content[PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_STORAGE_SLOT_INDEX] = storage_slot;
    msg_content[PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RANDOMNESS_INDEX] = randomness;
    msg_content[PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_NOTE_COMPLETION_LOG_TAG_INDEX] = note_completion_log_tag;

    for i in 0..packed_private_content.len() {
        msg_content[PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN + i] = packed_private_content[i];
    }

    encode_message(
        PARTIAL_NOTE_PRIVATE_MSG_TYPE_ID,
        // Notes use the note type id for metadata
        PartialNotePrivateContent::get_id() as u64,
        msg_content,
    )
}

/// Decodes the plaintext from a private note message (i.e. one of type [`PARTIAL_NOTE_PRIVATE_MSG_TYPE_ID`]).
///
/// This plaintext is meant to have originated from [`encode_partial_note_private_message`].
///
/// Note that while [`encode_partial_note_private_message`] returns a fixed-size array, this function takes a
/// [`BoundedVec`] instead. This is because when decoding we're typically processing runtime-sized plaintexts, more
/// specifically, those that originate from
/// [`crate::messages::encryption::message_encryption::MessageEncryption::decrypt`].
pub(crate) unconstrained fn decode_partial_note_private_message(
    msg_metadata: u64,
    msg_content: BoundedVec<Field, MAX_MESSAGE_CONTENT_LEN>,
) -> (AztecAddress, Field, Field, Field, Field, BoundedVec<Field, MAX_PARTIAL_NOTE_PRIVATE_PACKED_LEN>) {
    let note_type_id = msg_metadata as Field; // TODO: make note type id not be a full field

    // The following ensures that the message content contains at least the minimum number of fields required for a
    // valid partial note private message. (Refer to the description of
    // PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_NON_NOTE_FIELDS_LEN for more information about these fields.)
    assert(
        msg_content.len() >= PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN,
        f"Invalid private note message: all partial note private messages must have at least {PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN} fields",
    );

    // If PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_NON_NOTE_FIELDS_LEN is changed, causing the assertion below to fail, then
    // the destructuring of the partial note private message encoding below must be updated as well.
    std::static_assert(
        PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN == 4,
        "unexpected value for PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_NON_NOTE_FIELDS_LEN",
    );

    // We currently have four fields that are not the partial note's packed representation, which are the owner, the
    // storage slot, the randomness, and the note completion log tag.
    let owner = AztecAddress::from_field(
        msg_content.get(PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_OWNER_INDEX),
    );
    let storage_slot = msg_content.get(PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_STORAGE_SLOT_INDEX);
    let randomness = msg_content.get(PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RANDOMNESS_INDEX);
    let note_completion_log_tag = msg_content.get(PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_NOTE_COMPLETION_LOG_TAG_INDEX);

    let packed_private_note_content: BoundedVec<Field, MAX_PARTIAL_NOTE_PRIVATE_PACKED_LEN> = array::subbvec(
        msg_content,
        PARTIAL_NOTE_PRIVATE_MSG_PLAINTEXT_RESERVED_FIELDS_LEN,
    );

    (owner, storage_slot, randomness, note_completion_log_tag, note_type_id, packed_private_note_content)
}

mod test {
    use crate::{
        messages::{
            encoding::decode_message,
            logs::partial_note::{decode_partial_note_private_message, encode_partial_note_private_message},
            msg_type::PARTIAL_NOTE_PRIVATE_MSG_TYPE_ID,
        },
        note::note_interface::NoteType,
    };
    use crate::protocol::{address::AztecAddress, traits::{FromField, Packable}};
    use crate::test::mocks::mock_note::MockNote;

    global VALUE: Field = 7;
    global OWNER: AztecAddress = AztecAddress::from_field(8);
    global STORAGE_SLOT: Field = 9;
    global RANDOMNESS: Field = 10;
    global NOTE_COMPLETION_LOG_TAG: Field = 11;

    #[test]
    unconstrained fn encode_decode() {
        // Note that here we use MockNote as the private fields of a partial note
        let note = MockNote::new(VALUE).build_note();

        let message_plaintext = encode_partial_note_private_message(
            note,
            OWNER,
            STORAGE_SLOT,
            RANDOMNESS,
            NOTE_COMPLETION_LOG_TAG,
        );

        let (msg_type_id, msg_metadata, msg_content) = decode_message(BoundedVec::from_array(message_plaintext));

        assert_eq(msg_type_id, PARTIAL_NOTE_PRIVATE_MSG_TYPE_ID);

        let (owner, storage_slot, randomness, note_completion_log_tag, note_type_id, packed_note) =
            decode_partial_note_private_message(msg_metadata, msg_content);

        assert_eq(note_type_id, MockNote::get_id());
        assert_eq(owner, OWNER);
        assert_eq(storage_slot, STORAGE_SLOT);
        assert_eq(randomness, RANDOMNESS);
        assert_eq(note_completion_log_tag, NOTE_COMPLETION_LOG_TAG);
        assert_eq(packed_note, BoundedVec::from_array(note.pack()));
    }
}

147.source:
pub(crate) mod event_validation_request;

mod message_context;
pub use message_context::MessageContext;

pub(crate) mod note_validation_request;
pub(crate) mod log_retrieval_request;
pub(crate) mod log_retrieval_response;
pub(crate) mod pending_tagged_log;

use crate::{
    capsules::CapsuleArray,
    event::EventSelector,
    messages::{
        discovery::partial_notes::DeliveredPendingPartialNote,
        logs::{event::MAX_EVENT_SERIALIZED_LEN, note::MAX_NOTE_PACKED_LEN},
        processing::{
            log_retrieval_request::LogRetrievalRequest, log_retrieval_response::LogRetrievalResponse,
            note_validation_request::NoteValidationRequest, pending_tagged_log::PendingTaggedLog,
        },
    },
    oracle,
};
use crate::protocol::{address::AztecAddress, hash::sha256_to_field};
use event_validation_request::EventValidationRequest;

// Base slot for the pending tagged log array to which the fetch_tagged_logs oracle inserts found private logs.
global PENDING_TAGGED_LOG_ARRAY_BASE_SLOT: Field =
    sha256_to_field("AZTEC_NR::PENDING_TAGGED_LOG_ARRAY_BASE_SLOT".as_bytes());

global NOTE_VALIDATION_REQUESTS_ARRAY_BASE_SLOT: Field = sha256_to_field(
    "AZTEC_NR::NOTE_VALIDATION_REQUESTS_ARRAY_BASE_SLOT".as_bytes(),
);

global EVENT_VALIDATION_REQUESTS_ARRAY_BASE_SLOT: Field = sha256_to_field(
    "AZTEC_NR::EVENT_VALIDATION_REQUESTS_ARRAY_BASE_SLOT".as_bytes(),
);

global LOG_RETRIEVAL_REQUESTS_ARRAY_BASE_SLOT: Field = sha256_to_field(
    "AZTEC_NR::LOG_RETRIEVAL_REQUESTS_ARRAY_BASE_SLOT".as_bytes(),
);

global LOG_RETRIEVAL_RESPONSES_ARRAY_BASE_SLOT: Field = sha256_to_field(
    "AZTEC_NR::LOG_RETRIEVAL_RESPONSES_ARRAY_BASE_SLOT".as_bytes(),
);

/// Searches for private logs emitted by `contract_address` that might contain messages for one of the local accounts,
/// and stores them in a `CapsuleArray` which is then returned.
pub(crate) unconstrained fn get_private_logs(contract_address: AztecAddress) -> CapsuleArray<PendingTaggedLog> {
    // We will eventually perform log discovery via tagging here, but for now we simply call the `fetchTaggedLogs`
    // oracle. This makes PXE synchronize tags, download logs and store the pending tagged logs in a capsule array.
    oracle::message_processing::fetch_tagged_logs(PENDING_TAGGED_LOG_ARRAY_BASE_SLOT);

    CapsuleArray::at(contract_address, PENDING_TAGGED_LOG_ARRAY_BASE_SLOT)
}

/// Enqueues a note for validation by PXE, so that it becomes aware of a note's existence allowing for later retrieval
/// via `get_notes` oracle. The note will be scoped to `contract_address`, meaning other contracts will not be able to
/// access it unless authorized.
///
/// In order for the note validation and insertion to occur, `validate_and_store_enqueued_notes_and_events` must be
/// later called. For optimal performance, accumulate as many note validation requests as possible and then validate
/// them all at the end (which results in PXE minimizing the number of network round-trips).
///
/// The `packed_note` is what `getNotes` will later return. PXE indexes notes by `storage_slot`, so this value is
/// typically used to filter notes that correspond to different state variables. `note_hash` and `nullifier` are the
/// inner hashes, i.e. the raw hashes returned by `NoteHash::compute_note_hash` and `NoteHash::compute_nullifier`. PXE
/// will verify that the siloed unique note hash was inserted into the tree at `tx_hash`, and will store the nullifier
/// to later check for nullification.
///
/// `owner` is the address used in note hash and nullifier computation, often requiring knowledge of their nullifier
/// secret key.
///
/// `recipient` is the account to which the note message was delivered (i.e. the address the message was encrypted to).
/// This determines which PXE account can see the note - other accounts will not be able to access it (e.g. other
/// accounts will not be able to see one another's token balance notes, even in the same PXE) unless authorized. In
/// most cases `recipient` equals `owner`, but they can differ in scenarios like delegated discovery.
pub(crate) unconstrained fn enqueue_note_for_validation(
    contract_address: AztecAddress,
    owner: AztecAddress,
    storage_slot: Field,
    randomness: Field,
    note_nonce: Field,
    packed_note: BoundedVec<Field, MAX_NOTE_PACKED_LEN>,
    note_hash: Field,
    nullifier: Field,
    tx_hash: Field,
    recipient: AztecAddress,
) {
    // We store requests in a `CapsuleArray`, which PXE will later read from and deserialize into its version of the
    // Noir `NoteValidationRequest`
    CapsuleArray::at(contract_address, NOTE_VALIDATION_REQUESTS_ARRAY_BASE_SLOT).push(
        NoteValidationRequest {
            contract_address,
            owner,
            storage_slot,
            randomness,
            note_nonce,
            packed_note,
            note_hash,
            nullifier,
            tx_hash,
            recipient,
        },
    )
}

/// Enqueues an event for validation by PXE, so that it can be efficiently validated and then inserted into the event
/// store.
///
/// In order for the event validation and insertion to occur, `validate_and_store_enqueued_notes_and_events` must be
/// later called. For optimal performance, accumulate as many event validation requests as possible and then validate
/// them all at the end (which results in PXE minimizing the number of network round-trips).
pub(crate) unconstrained fn enqueue_event_for_validation(
    contract_address: AztecAddress,
    event_type_id: EventSelector,
    randomness: Field,
    serialized_event: BoundedVec<Field, MAX_EVENT_SERIALIZED_LEN>,
    event_commitment: Field,
    tx_hash: Field,
    recipient: AztecAddress,
) {
    // We store requests in a `CapsuleArray`, which PXE will later read from and deserialize into its version of the
    // Noir `EventValidationRequest`
    CapsuleArray::at(contract_address, EVENT_VALIDATION_REQUESTS_ARRAY_BASE_SLOT).push(
        EventValidationRequest {
            contract_address,
            event_type_id,
            randomness,
            serialized_event,
            event_commitment,
            tx_hash,
            recipient,
        },
    )
}

/// Validates all note and event validation requests enqueued via `enqueue_note_for_validation` and
/// `enqueue_event_for_validation`, inserting them into the note database and event store respectively, making them
/// queryable via `get_notes` oracle and our TS API (PXE::getPrivateEvents).
///
/// This automatically clears both validation request queues, so no further work needs to be done by the caller.
pub unconstrained fn validate_and_store_enqueued_notes_and_events(contract_address: AztecAddress) {
    oracle::message_processing::validate_and_store_enqueued_notes_and_events(
        contract_address,
        NOTE_VALIDATION_REQUESTS_ARRAY_BASE_SLOT,
        EVENT_VALIDATION_REQUESTS_ARRAY_BASE_SLOT,
    );
}

/// Efficiently queries the node for logs that result in the completion of all `DeliveredPendingPartialNote`s stored in
/// a `CapsuleArray` by performing all node communication concurrently. Returns a second `CapsuleArray` with Options
/// for the responses that correspond to the pending partial notes at the same index.
///
/// For example, given an array with pending partial notes `[ p1, p2, p3 ]`, where `p1` and `p3` have corresponding
/// completion logs but `p2` does not, the returned `CapsuleArray` will have contents `[some(p1_log), none(),
/// some(p3_log)]`.
pub(crate) unconstrained fn get_pending_partial_notes_completion_logs(
    contract_address: AztecAddress,
    pending_partial_notes: CapsuleArray<DeliveredPendingPartialNote>,
) -> CapsuleArray<Option<LogRetrievalResponse>> {
    let log_retrieval_requests = CapsuleArray::at(contract_address, LOG_RETRIEVAL_REQUESTS_ARRAY_BASE_SLOT);

    // We create a LogRetrievalRequest for each PendingPartialNote in the CapsuleArray. Because we need the indices in
    // the request array to match the indices in the partial note array, we can't use CapsuleArray::for_each, as that
    // function has arbitrary iteration order. Instead, we manually iterate the array from the beginning and push into
    // the requests array, which we expect to be empty.
    let mut i = 0;
    let pending_partial_notes_count = pending_partial_notes.len();
    while i < pending_partial_notes_count {
        let pending_partial_note = pending_partial_notes.get(i);
        log_retrieval_requests.push(
            LogRetrievalRequest { contract_address, unsiloed_tag: pending_partial_note.note_completion_log_tag },
        );
        i += 1;
    }

    oracle::message_processing::bulk_retrieve_logs(
        contract_address,
        LOG_RETRIEVAL_REQUESTS_ARRAY_BASE_SLOT,
        LOG_RETRIEVAL_RESPONSES_ARRAY_BASE_SLOT,
    );

    CapsuleArray::at(contract_address, LOG_RETRIEVAL_RESPONSES_ARRAY_BASE_SLOT)
}

