/**
 * Stores a single value in LMDB.
 */ export class LmdbAztecSingleton {
    #db;
    #slot;
    constructor(db, name){
        this.#db = db;
        this.#slot = [
            'singleton',
            name,
            'value'
        ];
    }
    get() {
        return this.#db.get(this.#slot);
    }
    getAsync() {
        return Promise.resolve(this.get());
    }
    set(val) {
        return this.#db.put(this.#slot, val);
    }
    delete() {
        return this.#db.remove(this.#slot);
    }
}
