import { Fr } from '@aztec/foundation/curves/bn254';
import { VerificationKeyAsFields, VerificationKeyData } from '@aztec/stdlib/vks';
export function abiToVKData(json) {
    const { verificationKey } = json;
    return new VerificationKeyData(new VerificationKeyAsFields(verificationKey.fields.map((str)=>Fr.fromHexString(str)), Fr.fromHexString(verificationKey.hash)), Buffer.from(verificationKey.bytes, 'hex'));
}
