        0,
        0,
    );

    let empty_l2_to_l1_msg_hash_from_ts =
        0x003b18c58c739716e76429634a61375c45b3b5cd470c22ab6d3e14cee23dd992;

    assert_eq(res, empty_l2_to_l1_msg_hash_from_ts);
}

#[test]
