import { z } from 'zod';
import { schemas, zodFor } from '../../schemas/index.js';
export const ContractInstanceUpdateSchema = zodFor()(z.object({
    prevContractClassId: schemas.Fr,
    newContractClassId: schemas.Fr,
    timestampOfChange: schemas.BigInt
}));
export const ContractInstanceUpdateWithAddressSchema = zodFor()(ContractInstanceUpdateSchema.and(z.object({
    address: schemas.AztecAddress
})));
